BlackBox.template:
// blockRamFile begin
reg ~TYPO ~GENSYM[RAM][0] [0:~LIT[4]-1];

initial begin
  $readmemb(~FILE[~LIT[5]],~SYM[0]);
end
~IF ~ISENABLED[3] ~THEN
always @(posedge ~ARG[2]) begin : ~GENSYM[~COMPNAME_blockRamFile][2]~IF ~VIVADO ~THEN
  if (~ARG[3]) begin
    if (~ARG[7]) begin
      ~SYM[0][~ARG[8]] <= ~ARG[9];
    end
    ~RESULT <= ~SYM[0][~ARG[6]];
  end~ELSE
  if (~ARG[7] & ~ARG[3]) begin
    ~SYM[0][~ARG[8]] <= ~ARG[9];
  end
  if (~ARG[3]) begin
    ~RESULT <= ~SYM[0][~ARG[6]];
  end~FI
end~ELSE
always @(posedge ~ARG[2]) begin : ~SYM[2]
  if (~ARG[7]) begin
    ~SYM[0][~ARG[8]] <= ~ARG[9];
  end
  ~RESULT <= ~SYM[0][~ARG[6]];
end~FI
// blockRamFile end
